without_steering_implementation:
module TopModule (
    input [2:0] in,
    output [1:0] out
);

    // Initialize the counter to zero
    reg [1:0] count = 2'b0;

    // Iterate over each bit of the input
    always @* begin
        // Reset the count if any bit is set to 1
        if (in[2]) count = 2'b0;
        else if (in[1]) count = count + 2'b01;
        else if (in[0]) count = count + 2'b10;
    end

    // Assign the count to the output
    assign out = count;

endmodule

