without_steering_implementation:
module TopModule_tb;

reg in1;
reg in2;
wire out;

TopModule top_module (
    .in1(in1),
    .in2(in2),
    .out(out)
);

initial begin
    // Test case 1: Both inputs are 0
    in1 = 0;
    in2 = 0;
    #10;
    $display("Test case 1: in1=0, in2=0, out=%b", out);
    assert(out == 1'b1);

    // Test case 2: in1 is 0, in2 is 1
    in1 = 0;
    in2 = 1;
    #10;
    $display("Test case 2: in1=0, in2=1, out=%b", out);
    assert(out == 1'b0);

    // Test case 3: in1 is 1, in2 is 0
    in1 = 1;
    in2 = 0;
    #10;
    $display("Test case 3: in1=1, in2=0, out=%b", out);
    assert(out == 1'b0);

    // Test case 4: Both inputs are 1
    in1 = 1;
    in2 = 1;
    #10;
    $display("Test case 4: in1=1, in2=1, out=%b", out);
    assert(out == 1'b0);

    $finish;
end

endmodule

