module TopModule(
    input  in     [15:0],
    input  control [3:0],
    output out_hi [7:0],
    output out_lo [7:0]
);

    // Split the input into two half-words
    assign out_hi = in[15:8];
    assign out_lo = in[7:0];

    // Output the selected byte based on the control signal
    assign out_hi = (control == 4'b1) ? in[15:8] : out_hi;
    assign out_lo = (control == 4'b0) ? in[7:0] : out_lo;

endmodule
