module TopModule (
    input in[2:0],
    input reset,
    output out[1:0]
);

    reg [1:0] count;

    always @(posedge reset or posedge in) begin
        if (reset) begin
            count <= 2'b0;
        end else begin
            count <= count + 1'b1;
        end
    end

    assign out[1] = |count; // OR all bits in count array
    assign out[0] = count[1] & count[0]; // AND the last two bits

endmodule

