with_steering_implementation:
module TopModule (
    input  in [2:0], // 3-bit input
    input  reset,   // Reset signal
    output out [1:0] // 2-bit output
);

    // Initialize counter to 0
    reg [1:0] count = 2'b0;

    // Iterate over each bit of the input
    always @(posedge clk or posedge reset) begin
        if (reset) begin
            // Reset the count when the reset signal is high
            count <= 2'b0;
        end else begin
            // Increment count if the current bit is '1'
            count = (in[2] | in[1] | in[0]) ? count + 1 : count;
        end
    end

    // Assign the count to the output
    assign out = count;

endmodule

