module TopModuleTestbench;

    // Instantiate the TopModule
    TopModule top_module (
        .in(1'b0),      // Input signal
        .out(out)       // Output signal
    );

    // Define the expected output
    reg expected_out;

    // Define the test cases
    initial begin
        // Test case 1: Input = 0, Expected Output = 0
        in = 1'b0;
        expected_out = 1'b0;
        #10; // Wait for 10 cycles
        assert (out == expected_out) else $error("Test case 1 failed");

        // Test case 2: Input = 1, Expected Output = 1
        in = 1'b1;
        expected_out = 1'b1;
        #10; // Wait for 10 cycles
        assert (out == expected_out) else $error("Test case 2 failed");
    end

endmodule

