module TopModule (
    input [31:0] in,
    output reg [31:0] out
);

    // Create a temporary array to hold each byte
    reg [7:0] byte[3];

    // Check if the input is within the expected range
    always @(*) begin
        if (in < 0 || in > 2**32-1) begin
            // Handle the error or exception
            out = 32'hFF; // Set output to all ones as an error indicator
            return;
        end
    end

    // Reverse the byte order by storing each byte in the correct position
    assign byte[0] = in[7:0];
    assign byte[1] = in[15:8];
    assign byte[2] = in[23:16];
    assign byte[3] = in[31:24];

    // Combine the bytes back into a single 32-bit word
    assign out = {byte[3], byte[2], byte[1], byte[0]};

endmodule

