module FourBitFullAdder(
    input [3:0] a,
    input [3:0] b,
    input cin,
    output [3:0] sum,
    output cout
);

    wire s0, s1, s2, s3;
    wire c0, c1, c2;

    // First level of full adders
    assign s0 = a[0] ^ b[0] ^ cin;
    assign c0 = (a[0] & b[0]) | (a[0] & cin) | (b[0] & cin);

    // Second level of full adders
    assign s1 = a[1] ^ b[1] ^ c0;
    assign c1 = (a[1] & b[1]) | (a[1] & c0) | (b[1] & c0);

    // Third level of full adders
    assign s2 = a[2] ^ b[2] ^ c1;
    assign c2 = (a[2] & b[2]) | (a[2] & c1) | (b[2] & c1);

    // Fourth level of full adders
    assign s3 = a[3] ^ b[3] ^ c2;
    assign cout = (a[3] & b[3]) | (a[3] & c2) | (b[3] & c2);

    // Assigning the sum and carry outputs
    assign sum = {s3, s2, s1, s0};

endmodule

