module TopModule (
    input wire in,
    output reg [7:0] out,
    output reg [7:0] counter
);

    reg [7:0] last_in = 8'b0;
    reg [7:0] current_counter = 8'b0;

    always @(posedge in) begin
        if (in == 1'b0 && last_in == 1'b1) begin
            current_counter = current_counter + 1'b1;
            if (current_counter > 8'b0) begin
                current_counter = 8'b0;
            end
        end else begin
            current_counter = 8'b0;
        end

        last_in = in;
        out = ~in;
    end

endmodule

