module TopModule (
    input [3:0] in,
    output [1:0] out
);

    // Initialize the counter to zero
    reg [1:0] count = 2'b00;

    // Iterate over each bit of the input
    always @(*) begin
        // Increment the counter if the current bit is '1'
        if (in[3]) count = count + 2'b01;
        if (in[2]) count = count + 2'b01;
        if (in[1]) count = count + 2'b01;
        if (in[0]) count = count + 2'b01;
    end

    // Assign the result to the output
    assign out = count;

endmodule

