module TopModule (
    input wire x,
    input wire y,
    input wire w,
    output wire z
);

    // XOR operation for x and y
    wire xor_result;
    assign xor_result = x ^ y;

    // XOR operation for xor_result and w
    wire xor_result_w;
    assign xor_result_w = xor_result ^ w;

    // AND operation
    assign z = xor_result_w & x;

endmodule

