module ParityChecker (
    input [N-1:0] data_in,  // Input vector of N bits
    output reg parity_out   // Output bit representing parity
);

    // Number of bits in the input vector
    parameter N = 8;

    // Declare a register to hold the parity bit
    reg parity_reg;

    // Logic to calculate the parity
    always @(*) begin
        // Initialize the parity_reg to 0
        parity_reg = 0;

        // Iterate over each bit in the data_in vector
        for (int i = 0; i < N; i++) begin
            // Add the current bit to the parity_reg
            parity_reg = parity_reg + data_in[i];
        end

        // Calculate the final parity by taking the modulo 2 of parity_reg
        parity_out = parity_reg % 2;
    end

endmodule

