module TopModule (
    input wire x,
    input wire y,
    input wire w,
    output reg v
);

always @(*) begin
    // Calculate the intermediate result of (w^x)
    wire intermediate = w ^ x;

    // Calculate the final result of (w^x) & y
    v = intermediate & y;
end

endmodule

