module TopModule (
    input  in [2:0], // 3-bit input
    output out [1:0] // 2-bit output
);

    // Declare a register to store the count of '1's
    reg [1:0] count;

    // Implement the population count logic
    always @* begin
        // Initialize the count to 0
        count = 2'b00;

        // Iterate over each bit of the input
        for (int i = 0; i < 3; i++) begin
            // Increment the count if the current bit is '1'
            if (in[i]) begin
                count = count + 2'b01;
            end
        end
    end

    // Assign the count to the output
    assign out = count;

endmodule

