module Adder_8bit (
    input [7:0] a, // First 8-bit input
    input [7:0] b, // Second 8-bit input
    output [7:0] sum // Output sum of the two inputs
);

    // Declare intermediate signals for carry propagation
    wire [7:0] carry_in;
    wire [7:0] carry_out;

    // Adder stage 1 (first 4 bits)
    adder4bit adder1 (
        .a(a[3:0]),
        .b(b[3:0]),
        .carry_in(carry_in[3:0]),
        .sum(sum[3:0]),
        .carry_out(carry_out[3:0])
    );

    // Adder stage 2 (next 4 bits)
    adder4bit adder2 (
        .a(a[7:4]),
        .b(b[7:4]),
        .carry_in(carry_out[3:0]),
        .sum(sum[7:4]),
        .carry_out(carry_out[4:0])
    );

endmodule

// Sub-module for adding 4-bit numbers
module adder4bit (
    input [3:0] a,
    input [3:0] b,
    input [3:0] carry_in,
    output [3:0] sum,
    output [3:0] carry_out
);

    // Declare internal signals
    wire [3:0] temp_sum;
    wire [3:0] temp_carry;

    // Full adder for each bit position
    full_adder fa0 (
        .a(a[0]),
        .b(b[0]),
        .cin(carry_in[0]),
        .sum(temp_sum[0]),
        .cout(temp_carry[0])
    );

    full_adder fa1 (
        .a(a[1]),
        .b(b[1]),
        .cin(temp_carry[0]),
        .sum(temp_sum[1]),
        .cout(temp_carry[1])
    );

    full_adder fa2 (
        .a(a[2]),
        .b(b[2]),
        .cin(temp_carry[1]),
        .sum(temp_sum[2]),
        .cout(temp_carry[2])
    );

    full_adder fa3 (
        .a(a[3]),
        .b(b[3]),
        .cin(temp_carry[2]),
        .sum(temp_sum[3]),
        .cout(carry_out[0])
    );

    // Assign the final sum and carry out
    assign sum = temp_sum;
    assign carry_out[3:1] = temp_carry;

endmodule

// Sub-module for a single-bit full adder
module full_adder (
    input a,
    input b,
    input cin,
    output sum,
    output cout
);

    // Sum and carry generation using XOR and AND gates
    assign sum = a ^ b ^ cin;
    assign cout = (a & b) | (a & cin) | (b & cin);

endmodule

