module TopModule (
    input  clk,
    input  rst_n,
    output valid
);

    reg [15:0] count;

    always @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            count <= 0;
        end else begin
            if (count < 15) begin
                count <= count + 1;
            end else begin
                count <= 0;
            end
        end
    end

    assign valid = (count == 15) ? 1'b1 : 1'b0;

endmodule

