module TopModule (
    input [31:0] in,
    output [31:0] out
);

    // Create a temporary wire to hold the reversed bytes
    reg [31:0] temp;

    // Assign the reversed byte order to the output
    assign out = temp;

    // Use a process to reverse the byte order
    always @* begin
        // Initialize the temporary wire to zero
        temp = 32'h0;

        // Reverse the byte order by shifting and ORing
        temp[31:24] = in[7:0];
        temp[23:16] = in[15:8];
        temp[15:8] = in[23:16];
        temp[7:0] = in[31:24];
    end

endmodule

