// Counter module
module SimpleCounter (
    input clk,
    input rst,
    output reg [3:0] count
);

always @(posedge clk or posedge rst) begin
    if (rst) begin
        count <= 4'b0000; // Reset to 0
    end else begin
        count <= count + 1'b1; // Increment by 1
    end
end

endmodule

// Testbench for SimpleCounter
module SimpleCounter_tb;
    reg clk;
    reg rst;
    wire [3:0] count;

    SimpleCounter uut (
        .clk(clk),
        .rst(rst),
        .count(count)
    );

    initial begin
        clk = 0;
        rst = 1; // Start with reset
        #5 rst = 0; // Clear reset

        // Simulate clock for 10 cycles
        for (int i = 0; i < 10; i++) begin
            clk = ~clk;
            #10;
        end

        $finish;
    end

endmodule

