module TopModule (
    input  wire clk,
    input  wire reset,
    input  wire in,
    output reg out,
    output reg counter
);

    always @(posedge clk or posedge reset) begin
        if (reset) begin
            out <= 8'b0;
            counter <= 8'b0;
        end else begin
            if (in) begin
                counter <= counter + 1;
                out <= ~counter;
            end else begin
                out <= ~counter;
            end
        end
    end

endmodule

