    TopModule top_module (
        .a(1'b0),
        .b(1'b0),
        .out(out)
    );

    // Declare the expected output signals
    reg expected_out;

    // Clock signal
    reg clk;
    initial begin
        clk = 1'b0;
        forever #5 clk = ~clk;
    end

    // Reset signal
    reg rst;
    initial begin
        rst = 1'b1;
        #10 rst = 1'b0;
    end

    // Test cases
    initial begin
        // Test case 1: a=0, b=0
        expected_out = 1'b0;
        #10;
        assert (out == expected_out) else $error("Test case 1 failed");

        // Test case 2: a=0, b=1
        expected_out = 1'b0;
        #10;
        assert (out == expected_out) else $error("Test case 2 failed");

        // Test case 3: a=1, b=0
        expected_out = 1'b0;
        #10;
        assert (out == expected_out) else $error("Test case 3 failed");

        // Test case 4: a=1, b=1
        expected_out = 1'b1;
        #10;
        assert (out == expected_out) else $error("Test case 4 failed");

        $finish;
    end

    // Monitor the output signal
    always @(posedge clk or posedge rst) begin
        if (rst) begin
            out <= 1'b0;
        end else begin
            out <= expected_out;
        end
    end

