without_steering_implementation:
module BinaryAdder (
    input [7:0] a,
    input [7:0] b,
    output reg [7:0] sum,
    output reg carry
);

    // Initialize carry to zero
    reg carry_in = 0;

    // Define full adder logic
    always @(*) begin
        sum = a + b + carry_in;
        carry = (a & b) | (carry_in & (a ^ b));
    end

endmodule

