// Testbench for TopModule
module TopModule_tb;
    reg [15:0] in;
    wire [7:0] out_hi;
    wire [7:0] out_lo;

    TopModule top_module (
        .in(in),
        .out_hi(out_hi),
        .out_lo(out_lo)
    );

    initial begin
        // Test case 1: in = 0x1234
        in = 16'h1234;
        #10; // Wait for 10 cycles
        $display("Test case 1: in = 0x%h, out_hi = 0x%h, out_lo = 0x%h", in, out_hi, out_lo);
        assert(out_hi == 8'h34 && out_lo == 8'h12) else $error("Test case 1 failed");

        // Test case 2: in = 0x5678
        in = 16'h5678;
        #10; // Wait for 10 cycles
        $display("Test case 2: in = 0x%h, out_hi = 0x%h, out_lo = 0x%h", in, out_hi, out_lo);
        assert(out_hi == 8'h78 && out_lo == 8'h56) else $error("Test case 2 failed");

        // Test case 3: in = 0x9ABC
        in = 16'h9ABC;
        #10; // Wait for 10 cycles
        $display("Test case 3: in = 0x%h, out_hi = 0x%h, out_lo = 0x%h", in, out_hi, out_lo);
        assert(out_hi == 8'hBC && out_lo == 8'h9A) else $error("Test case 3 failed");

        // Test case 4: in = 0xDEF0
        in = 16'hDEF0;
        #10; // Wait for 10 cycles
        $display("Test case 4: in = 0x%h, out_hi = 0x%h, out_lo = 0x%h", in, out_hi, out_lo);
        assert(out_hi == 8'hF0 && out_lo == 8'hDE) else $error("Test case 4 failed");

        // Test case 5: in = 0x0000
        in = 16'h0000;
        #10; // Wait for 10 cycles
        $display("Test case 5: in = 0x%h, out_hi = 0x%h, out_lo = 0x%h", in, out_hi, out_lo);
        assert(out_hi == 8'h00 && out_lo == 8'h00) else $error("Test case 5 failed");

        $finish;
    end
endmodule
