module TopModule (
    input  [31:0] in,
    output [31:0] out
);

    // Create an array of 4 bytes from the input
    reg [7:0] bytes[3];

    // Check if the input is within the valid range
    always @(*) begin
        if (in < 0 || in > 4294967295) begin
            $error("Input must be a valid 32-bit integer.");
            out = 32'b0; // Set output to 0 if input is invalid
        end else begin
            // Assign each byte to its respective position in the array
            assign bytes[0] = in[31:24];
            assign bytes[1] = in[23:16];
            assign bytes[2] = in[15:8];
            assign bytes[3] = in[7:0];

            // Create an array of 4 bytes for the output
            reg [7:0] reversed_bytes[3];

            // Reverse the order of the bytes
            assign reversed_bytes[0] = bytes[3];
            assign reversed_bytes[1] = bytes[2];
            assign reversed_bytes[2] = bytes[1];
            assign reversed_bytes[3] = bytes[0];

            // Combine the reversed bytes into a single 32-bit output
            assign out = {reversed_bytes[3], reversed_bytes[2], reversed_bytes[1], reversed_bytes[0]};
        end
    end

endmodule

