module ComplexCircuit (
    input clk,
    input reset,
    input [3:0] data_in,
    output reg [3:0] data_out
);

    // Register to store the previous state of data_in
    reg [3:0] prev_data_in;

    // Register to store the result of the AND gate
    reg [3:0] and_result;

    always @(posedge clk or posedge reset) begin
        if (reset) begin
            prev_data_in <= 4'b0;
            and_result <= 4'b0;
        end else begin
            prev_data_in <= data_in;
            and_result <= data_in & prev_data_in;
        end
    end

    // Conditional logic to determine the output
    always @(*) begin
        if (and_result == 4'b1000) begin
            data_out <= 4'b1111;
        end else if (and_result == 4'b0001) begin
            data_out <= 4'b0000;
        end else begin
            data_out <= and_result;
        end
    end

endmodule

