module TopModule(
    input [2:0] in,
    output reg [1:0] out
);

    // Initialize a counter to zero
    reg [1:0] count = 2'b0;

    // Iterate over each bit of the input
    always @(*) begin
        // Reset the count when the input changes
        if (in != in[2]) begin
            count = 2'b0;
        end else begin
            // Increment the count if the current bit is '1'
            if (in[2] == 1) begin
                count = count + 2'b1;
            end
        end
    end

    // Assign the count to the output
    assign out = count;

endmodule

