with_steering_implementation:
module TopModule(
    input wire reset,
    input wire enable,
    output reg out,
    output reg [3:0] counter
);

always @(posedge clk or posedge reset) begin
    if (reset) begin
        counter <= 4'b0;
    end else if (enable && counter < 15) begin
        counter <= counter + 1'b1;
    end else if (counter == 15) begin
        counter <= 4'b0;
    end
end

always @(*) begin
    out = counter[3];
end

endmodule

