with_steering_implementation:
module Adder16(
    input [15:0] A,
    input [15:0] B,
    output reg [15:0] Sum
);

    // Define a full adder module
    module FullAdder(
        input a,
        input b,
        input cin,
        output reg sum,
        output reg cout
    );

        always @(*) begin
            sum = a ^ b ^ cin;
            cout = (a & b) | (cin & (a ^ b));
        end

    endmodule

    // Declare variables for carry propagation
    reg [15:0] carry;
    reg [15:0] carry_next;

    // Initialize carry to zero
    assign carry = 0;

    // Generate full adders for each bit position
    generate
        for (genvar i = 0; i < 16; i++) begin
            FullAdder FA(
                .a(A[i]),
                .b(B[i]),
                .cin(carry[i]),
                .sum(Sum[i]),
                .cout(carry_next[i])
            );
        end
    endgenerate

    // Assign the final carry to the carry_next variable
    assign carry_next[15] = carry[15];

    // Assign the carry_next to the carry variable for the next iteration
    assign carry = carry_next;

endmodule

