);

    // Implement population count logic here

    // Initialize the output with zeros
    assign out = 2'b0;

    // Iterate over each bit of the input
    for (genvar i = 0; i < 3; i++) begin : bit_counter
        // Increment the output if the current bit is '1'
        if (in[i]) begin
            out = out + 1;
        end
    end

