module TopModule (
    input [2:0] in,
    output [1:0] out
);

    wire [2:0] temp;
    wire [1:0] sum;

    // XOR the input with a constant value of 3'b111
    temp = in ^ 3'b111;

    // AND the result with the input to count the number of '1's
    sum = temp & in;

    // The output is the count of '1's
    assign out = sum;

endmodule

