    input  in [2:0],  // 3-bit input vector
    output out [1:0]   // 2-bit output vector representing population count
);

reg [1:0] count;  // Register to store the population count

always @(*) begin
    count = 0;  // Initialize count to 0

    // Iterate over each bit of the input vector
    if (in[0]) count = count + 1;
    if (in[1]) count = count + 1;
    if (in[2]) count = count + 1;
end

assign out = count;  // Assign the count to the output vector

endmodule

