module TopModule (
    input wire clk,
    input wire reset,
    output reg out
);

reg [3:0] counter;

always @(posedge clk or posedge reset) begin
    if (reset) begin
        counter <= 4'b0;
        out <= 1'b0;
    end else begin
        counter <= counter + 1'b1;
        out <= counter[3];
    end
end

