module ParityChecker (
    input  [7:0] in,
    output reg [3:0] out
);

    // Count the number of '1's in the input
    reg [3:0] count;
    always @(*) begin
        count = 0;
        for (int i = 0; i < 8; i++) begin
            count += in[i];
        end
    end

    // Assign the output based on the parity of the count
    always @(*) begin
        if (count % 2 == 1) begin
            out = 4'b1111;
        end else begin
            out = 4'b0000;
        end
    end

endmodule

