    input [31:0] in,
    output reg [31:0] out
);

    // Check if the input is within the range of a 32-bit signed integer
    wire [31:0] temp = {32{in[31]}};
    wire overflow = temp != in;

    // Reverse the byte order of the input if there is no overflow
    assign out = overflow ? in : in[31:0] << 24 | in[23:16] << 16 | in[15:8] << 8 | in[7:0];

endmodule

