module full_adder(
    input a,
    input b,
    input cin,
    output sum,
    output cout
);

    // XOR operation for the sum bit
    xor sum_xor(a, b, sum);

    // AND operation for the carry-out bit
    and carry_and(a, b, cin);
    and carry_and2(a, cin, b);
    or carry_or(carry_and, carry_and2, cout);

endmodule

