module TopModule (
    input  [31:0] in,
    output [31:0] out
);

    // Create an array to hold each byte of the input
    reg [7:0] byte0, byte1, byte2, byte3;

    // Assign each byte to its respective register
    assign byte0 = in[7:0];
    assign byte1 = in[15:8];
    assign byte2 = in[23:16];
    assign byte3 = in[31:24];

    // Reverse the byte order by assigning each byte to its new position
    assign out[31:24] = byte0;
    assign out[23:16] = byte1;
    assign out[15:8] = byte2;
    assign out[7:0] = byte3;

    // Error Handling: Check if the input is within valid range
    always @(*) begin
        if (in < 0 || in > 2**32-1) begin
            $display("Error: Input value %b is out of valid range (0 to 2^32-1)", in);
            out = 32'b0; // Set output to zero on error
        end
    end

endmodule

