module TopModule (
    input  [2:0] in,
    output [1:0] out
);

    // Internal signals
    reg [1:0] count;
    reg [2:0] bin_count;

    // Count the number of '1's in the input vector
    always @(*) begin
        count = 0;
        for (int i = 0; i < 3; i++) begin
            if (in[i]) begin
                count = count + 1;
            end
        end
    end

    // Convert the count to binary
    always @(*) begin
        bin_count = {count[1], count[0]};
    end

    // Assign the binary count to the output
    assign out = bin_count;

endmodule

