module Counter (
    input clk,
    input reset,
    input [2:0] increment,
    output reg [3:0] count
);

always @(posedge clk or posedge reset) begin
    if (reset)
        count <= 4'b0;
    else if (increment > 0) // Only increment if increment signal is high
        count <= count + increment;
end

endmodule

