module TopModule (
    input x,
    input y,
    output z
);

    // XOR operation between x and y
    // x ^ y will be 1 if x and y are different, and 0 if they are the same
    wire xor_result = x ^ y;

    // AND operation between xor_result and x
    // This will be 1 only if both xor_result and x are 1
    assign z = xor_result & x;

endmodule

