module FourBitAdder (
    input [3:0] a,  // First 4-bit input
    input [3:0] b,  // Second 4-bit input
    output reg [3:0] sum,  // Sum of the two inputs
    output reg carry  // Carry-out from the addition
);

    // Define intermediate variables for each bit position
    reg [3:0] temp_sum;
    reg [2:0] temp_carry;

    // Add the least significant bits (LSBs)
    temp_sum[0] = a[0] ^ b[0];
    temp_carry[0] = a[0] & b[0];

    // Add the next bit position with the carry-in
    temp_sum[1] = a[1] ^ b[1] ^ temp_carry[0];
    temp_carry[1] = a[1] & b[1] | a[1] & temp_carry[0] | b[1] & temp_carry[0];

    // Add the next bit position with the carry-in from the previous addition
    temp_sum[2] = a[2] ^ b[2] ^ temp_carry[1];
    temp_carry[2] = a[2] & b[2] | a[2] & temp_carry[1] | b[2] & temp_carry[1];

    // Add the most significant bit (MSB) with the carry-in from the previous addition
    temp_sum[3] = a[3] ^ b[3] ^ temp_carry[2];
    carry = a[3] & b[3] | a[3] & temp_carry[2] | b[3] & temp_carry[2];

    // Assign the final sum and carry-out
    sum = temp_sum;

endmodule

