module TopModule(
    input  in[2:0],
    output out[1:0]
);

    wire [2:0] count;
    reg [15:0] delay_counter; // 16-bit counter for 10ns delay

    // Population count logic
    always @(*) begin
        count = 0;
        for (int i = 0; i <= 2; i++) begin
            if (in[i]) begin
                count = count + 1;
            end
        end
    end

    // Delay logic
    always @(posedge clk) begin // Assuming 'clk' is the clock signal
        if (delay_counter == 15'd10000000) begin // 10ns delay at 10MHz clock
            delay_counter <= 16'b0;
            out = count[1:0];
        end else begin
            delay_counter <= delay_counter + 1;
        end
    end

endmodule

