module TopModule (
    input [31:0] in,
    output [31:0] out
);

    // Create a temporary wire to hold the reversed bytes
    wire [31:0] temp;

    // Assign the reversed bytes to the temp wire
    assign temp = {
        in[7:0], in[15:8], in[23:16], in[31:24]
    };

    // Assign the reversed bytes to the out port
    assign out = temp;

endmodule

with_steering_implementation:
module TopModule (
    input  in [63:0], // 64-bit input
    output out [63:0] // 64-bit output
);

    wire [7:0] byte0, byte1, byte2, byte3, byte4, byte5, byte6, byte7;

    // Extract each byte from the input
    assign byte0 = in[7:0];
    assign byte1 = in[15:8];
    assign byte2 = in[23:16];
    assign byte3 = in[31:24];
    assign byte4 = in[39:32];
    assign byte5 = in[47:40];
    assign byte6 = in[55:48];
    assign byte7 = in[63:56];

    // Concatenate the bytes in reverse order
    assign out = {byte7, byte6, byte5, byte4, byte3, byte2, byte1, byte0};

endmodule

