with_steering_implementation:
module PulseGenerator (
    input wire clk,
    input wire reset,
    input wire input_signal,
    output reg pulse_out
);

    reg last_state;

    always @(posedge clk or posedge reset) begin
        if (reset) begin
            last_state <= 0;
            pulse_out <= 0;
        end else begin
            last_state <= input_signal;
        end
    end

    always @(posedge clk) begin
        if (last_state != input_signal) begin
            pulse_out <= 1;
        end else begin
            pulse_out <= 0;
        end
    end

endmodule

