// Testbench
module TopModule_tb;

    TopModule dut (
        .a(1'b0),
        .b(1'b0),
        .out(out)
    );

    initial begin
        $monitor("Time: %t, a=%b, b=%b, out=%b", $time, dut.a, dut.b, dut.out);

        // Test case 1: a=0, b=0
        dut.a <= 1'b0;
        dut.b <= 1'b0;
        #10; // Wait for 10 time units

        // Test case 2: a=0, b=1
        dut.a <= 1'b0;
        dut.b <= 1'b1;
        #10;

        // Test case 3: a=1, b=0
        dut.a <= 1'b1;
        dut.b <= 1'b0;
        #10;

        // Test case 4: a=1, b=1
        dut.a <= 1'b1;
        dut.b <= 1'b1;
        #10;

        $finish;
    end

endmodule

