without_steering_implementation:
module TopModule (
    input wire [15:0] in,
    input wire reset,
    output reg [7:0] out_hi,
    output reg [7:0] out_lo
);

always @(posedge reset or posedge clk) begin
    if (reset) begin
        out_hi <= 8'h00;
        out_lo <= 8'h00;
    end else begin
        out_hi <= in[15:8];
        out_lo <= in[7:0];
    end
end

endmodule
