    input [3:0] a, // 4-bit input A
    input [3:0] b, // 4-bit input B
    output reg [3:0] sum, // 4-bit sum output
    output reg [3:0] carry // 4-bit carry output
);

    // Initialize carry out to 0
    assign carry = 0;

    // Sum calculation using XOR and AND gates
    always @(*) begin
        sum = a ^ b;
        carry = (a & b) | (carry & (a ^ b));
    end

endmodule

