without_steering_implementation:
module TopModule (
    input  [2:0] in,
    output [1:0] out
);

    // Declare internal signals
    wire [2:0] temp;
    reg [2:0] count;

    // Implement population count logic
    assign temp = in; // Assign input to a temporary wire
    always @(temp) begin
        count = 0; // Reset count
        for (int i = 0; i < 3; i++) begin
            if (temp[i]) begin
                count = count + 1; // Increment count if bit is '1'
            end
        end
        out = count[1:0]; // Output the least significant two bits of count
    end

endmodule

