without_steering_implementation:
module TopModule (
    input  x,
    input  y,
    output z
);

always @(posedge clk or negedge rst_n) begin
    if (!rst_n) begin
        z <= 1'b0;
    end else begin
        #100ns; // Add a delay of 100ns
        z <= (x ^ y) & x;
    end
end

// Clock and reset signals must be provided externally
input clk;
input rst_n;

endmodule

