module TopModule (
    input  in  [2:0],
    output out [1:0]
);

    wire [2:0] cnt;

    // Initialize the counter
    reg [2:0] count = 0;

    // Increment the counter for each '1' in the input
    always @(in) begin
        if (in[0]) count = count + 1;
        if (in[1]) count = count + 1;
        if (in[2]) count = count + 1;
    end

    // Assign the lower two bits of the counter to the output
    assign out = count[1:0];

endmodule

