    input reset, // Reset signal
    output [1:0] out // 2-bit output
);

    wire [2:0] temp; // Temporary wire for population count

    // Population count logic
    assign temp = in[2] + in[1] + in[0];

    // Assign the population count to the output
    assign out = temp[1:0];

    // Reset logic
    always @(posedge reset) begin
        temp <= 0;
    end

