module TopModule(
    input [2:0] in,
    output [1:0] out
);

always @(*) begin
    // Initialize out to 0
    out = 2'b00;

    // Count the number of 1's in the input vector
    for (int i = 0; i < 3; i++) begin
        if (in[i]) begin
            out[1] = 1'b1;
            out[0] = out[1];
        end else begin
            out[1] = 1'b0;
            out[0] = out[1];
        end
    end
end

endmodule

